SELECT *  FROM t_refund rf left join t_responsibility rr on rr.`complaint_id` = rf.`pk_id`

  left join t_complaint_level cl ON rr.`level` = cl.parent_id

        rf.is_delete = 0

          AND rr.`parent_name` =#{parentName,jdbcType=VARCHAR}


          AND (rf.WangWangNum LI KE CONCAT('%', #{keyword}, '%')


          and rr.createTime >=  #{startTime}  and rr.createTime <=  #{endTime}


          AND rr.`result` =#{result,jdbcType=INTEGER}


          AND rr.`grade` =#{grade}





    rf.`is_delete` = 0

      AND rr.`wang_wang_num` LIKE CONCAT ("%",#{wangWangNum},"%")


      and rr.`create_time` >=  #{startTime}  and rr.`create_time` <=  #{endTime}


      AND rr.`result` =#{result,jdbcType=INTEGER}


      AND rr.`grade` =#{grade}



